CREATE TABLE PERSONA (CODPERSONA VARCHAR(255) NOT NULL, APELLIDO VARCHAR(255), DIRECCION VARCHAR(255), NOMBRE VARCHAR(255), NUMERODOCUMENTO VARCHAR(255), SALDODISPONIBLE DECIMAL(38), TELEFONO VARCHAR(255), TIPOPERSONA INTEGER, PRIMARY KEY (CODPERSONA))
CREATE TABLE PRODUCTO (CODPRODUCTO VARCHAR(255) NOT NULL, CANTIDAD DECIMAL(38), DESCRIPCION VARCHAR(255), NOMBRE VARCHAR(255), PRECIO DECIMAL(38), PRIMARY KEY (CODPRODUCTO))
CREATE TABLE REGISTROPAGO (CODPAGO VARCHAR(255) NOT NULL, CODPERSONA VARCHAR(255), ESTADO INTEGER, FECHA DATE, MENSAJE VARCHAR(255), MONTO DECIMAL(38), TIPO VARCHAR(255), PRIMARY KEY (CODPAGO))
CREATE TABLE COMPRA (NROFACTURA VARCHAR(10) NOT NULL, FECHA DATE NOT NULL, PROVEEDOR VARCHAR(255) NOT NULL, PRIMARY KEY (NROFACTURA))
CREATE TABLE COMPRADETALLE (ID INTEGER NOT NULL, CANTIDAD INTEGER NOT NULL, PRECIO FLOAT NOT NULL, COMPRA VARCHAR(10) NOT NULL, PRODUCTO VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE VENTA (NROFACTURA VARCHAR(10) NOT NULL, FECHA DATE NOT NULL, CLIENTE VARCHAR(255) NOT NULL, PRIMARY KEY (NROFACTURA))
CREATE TABLE VENTADETALLE (ID INTEGER NOT NULL, CANTIDAD INTEGER NOT NULL, PRECIO FLOAT NOT NULL, PRODUCTO VARCHAR(255) NOT NULL, VENTA VARCHAR(10) NOT NULL, PRIMARY KEY (ID))
ALTER TABLE COMPRA ADD CONSTRAINT FK_COMPRA_PROVEEDOR FOREIGN KEY (PROVEEDOR) REFERENCES PERSONA (CODPERSONA)
ALTER TABLE COMPRADETALLE ADD CONSTRAINT FK_COMPRADETALLE_PRODUCTO FOREIGN KEY (PRODUCTO) REFERENCES PRODUCTO (CODPRODUCTO)
ALTER TABLE COMPRADETALLE ADD CONSTRAINT FK_COMPRADETALLE_COMPRA FOREIGN KEY (COMPRA) REFERENCES COMPRA (NROFACTURA)
ALTER TABLE VENTA ADD CONSTRAINT FK_VENTA_CLIENTE FOREIGN KEY (CLIENTE) REFERENCES PERSONA (CODPERSONA)
ALTER TABLE VENTADETALLE ADD CONSTRAINT FK_VENTADETALLE_VENTA FOREIGN KEY (VENTA) REFERENCES VENTA (NROFACTURA)
ALTER TABLE VENTADETALLE ADD CONSTRAINT FK_VENTADETALLE_PRODUCTO FOREIGN KEY (PRODUCTO) REFERENCES PRODUCTO (CODPRODUCTO)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
